--Display the “FIRST_NAME” from Employee table using the alias name as Employee_name
select first_name as employee_name from employees

--Display “LAST_NAME” from Employee table in upper case.
select upper(last_name) from employees

--Display unique values of DEPARTMENT from EMPLOYEE table.
select distinct(department) as department from employees

-- Display the first three characters of LAST_NAME from EMPLOYEE table
select SUBSTRING(last_name,1,3) as name from employees

--Display the unique values of DEPARTMENT from EMPLOYEE table and prints its length.select len(distinct(department)) as department from employees--Display the FIRST_NAME and LAST_NAME from EMPLOYEE table into a single column AS FULL_NAME.select first_name+' '+last_name as full_name from employees--DISPLAY all EMPLOYEE details from the employee table order by FIRST_NAME Ascending.select * from employees order by first_name asc--. Display all EMPLOYEE details order by FIRST_NAME Ascending and DEPARTMENT Descendingselect * from employees order by first_name asc,department desc--Display details for EMPLOYEE with the first name as “VEENA” and “KARAN” from EMPLOYEE table.select * from employees where first_name in('VEENA','KARAN')--Display details of EMPLOYEE with DEPARTMENT name as “Admin”select * from employees where department in('Admin')--DISPLAY details of the EMPLOYEES whose FIRST_NAME contains ‘V’.select * from employees where first_name like'%v%'--DISPLAY details of the EMPLOYEES whose SALARY lies between 100000 and 500000. select * from employees where salary  between 100000 and 500000--Display details of the employees who have joined in Feb-2020select * from employees where year(convert(date,joining_date))=2020 and month(convert(date,joining_date))=2--Display employee names with salaries >= 50000 and <= 100000.select first_name from employees where salary >= 50000 and salary<= 100000--Display the number of Employees for each department in the descenting orderselect count(first_name) from employees group by department order by count(first_name) asc-- DISPLAY duplicate records having matching data in some fields of a table.select department from employees where count(department)>1--Display only odd rows from a table.select * from (select *, ROW_NUMBER()over (order by salary)as row_numberfrom programmer) row_tablewhere row_table.row_number%2=1--Clone a new table from EMPLOYEE tableselect *into Employee_newfromEmployee--DISPLAY the TOP 2 highest salary from a table.select top(2)* from employees order by salary desc--DISPLAY the list of employees with the same salaryselect *,ROW_NUMBER()Over(partition by salary order by salary desc)from employees--Display the second highest salary from a table.select top(1) salary from employees where salary in (select top(2) salary from employees order by salary desc) order by salary asc--Display the first 50% records from a tableselect top 50 percent * from employees--Display the departments that have less than 4 people in itselect department from employees group by department having count(first_name)<4--Display the first name and title of all the employeesselect emp.first_name,emp_title.employee_title from employee as emp join emp_title as emp_title on emp.employee_id=emp_title.employee_id--display the name of the employee who has got maximum bonusselect emp.first_name from employee as emp join emp_bonus as emp_bonus on emp.employee_id=emp_bonus.employee_id where emp_bonus.bonus = (select max(bonus) from emp_bonus)--Diplay the average salaries for each department select avg(salary),department from employee group by department